import axios from "axios";
import React, { useContext, useEffect } from "react";
import { GlobalContext } from "../../context/globalcontext";

const ListData = () => {
    const { state } = useContext(GlobalContext);

    let { data, setData } = state;

    useEffect(() => {
        axios
            .get(" https://dev-example.sanbercloud.com/api/job-vacancy")
            .then((res) => {
                // console.log(res.data.data);
                // let results = res.data.data;
                // setData([...results]);
                setData([...res.data.data]);
            })
            .catch((err) => {});
    }, []);

    const description = (str) => {
        if (str.length <= 20) {
            return str;
        }
        return str.slice(0, 20) + "...";
    };

    const currency = (number) => {
        if (number !== 0) {
            return new Intl.NumberFormat("id-ID", {
                style: "currency",
                currency: "IDR",
                maximumSignificantDigits: 3,
            }).format(number);
        } else {
            return "Free";
        }
    };

    return (
        <div className="relative overflow-x-auto shadow-md w-4/5">
            <table className="w-10 text-sm text-left text-gray-500 dark:text-gray-400">
                <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                    <tr>
                        <th scope="col" className="px-6 py-3">
                            No
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Logo
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Title
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Type
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Tenure
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Company
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Available
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Price
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Weight
                        </th>
                        <th scope="col" className="px-6 py-3">
                            Action
                        </th>
                    </tr>
                </thead>
                <tbody>
                    <tr className="bg-white border-b dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600">
                        <th scope="row" className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                            Apple MacBook Pro 17"
                        </th>
                        <td className="px-6 py-4">Sliver</td>
                        <td className="px-6 py-4">Laptop</td>
                        <td className="px-6 py-4">Yes</td>
                        <td className="px-6 py-4">Yes</td>
                        <td className="px-6 py-4">$2999</td>
                        <td className="px-6 py-4">3.0 lb.</td>
                        <td className="flex items-center px-6 py-4 space-x-3">
                            <a href="#" className="font-medium text-blue-600 dark:text-blue-500 hover:underline">
                                Edit
                            </a>
                            <a href="#" className="font-medium text-red-600 dark:text-red-500 hover:underline">
                                Remove
                            </a>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

export default ListData;
