import axios from "axios";
import Cookies from "js-cookie";
import React, { createContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export const GlobalContext = createContext();

export const GlobalProvider = (props) => {
    let navigate = useNavigate();

    const [data, setData] = useState(null);

    const [input, setInput] = useState({
        title: "",
        company_image_url: "",
        job_description: "",
        job_qualification: "",
        job_type: "",
        job_tenure: "",
        job_status: 0,
        company_name: "",
        company_city: "",
        salary_min: 0,
        salary_max: 0,
    });

    const [fetchStatus, setFetchStatus] = useState(true);

    const [token, setToken] = useState({});

    useEffect(() => {
        if (Cookies.get("token") !== undefined) {
            setToken(Cookies.get("token"));
        }
    }, []);
    // handleFunction
    const handleInput = (event) => {
        let name = event.target.name;
        let value = event.target.value;

        setInput({ ...input, [name]: value });
    };

    const handleDelete = (event) => {
        let idData = parseInt(event.target.value);

        console.log(idData);

        axios.delete(`https://dev-example.sanbercloud.com/api/job-vacancy/${idData}`, { headers: { Authorization: "Bearer " + { token } } }).then((res) => {
            setFetchStatus(true);
            navigate("/dashboard/list-job-vacancy");
        });
    };

    const handleSubmit = (event) => {
        event.preventDefaulth();

        let { title, company_image_url, job_description, job_qualification, job_type, job_tenure, job_status, company_name, company_city, salary_min, salary_max } = input;

        console.log(title);
        // axios
        //     .post(
        //         `https://dev-example.sanbercloud.com/api/job-vacancy`,
        //         { title, company_image_url, job_description, job_qualification, job_type, job_tenure, job_status, company_name, company_city, salary_min, salary_max },
        //         { headers: { Authorization: "Bearer " + Cookies.get("token") } }
        //     )
        //     .then((has) => {
        //         setFetchStatus(true);
        //     });
    };

    let state = {
        data,
        setData,
        input,
        setInput,
        fetchStatus,
        setFetchStatus,
    };

    let handleFunction = {
        handleDelete,
        handleInput,
        handleSubmit,
    };

    return (
        <GlobalContext.Provider
            value={{
                state,
                handleFunction,
            }}
        >
            {props.children}
        </GlobalContext.Provider>
    );
};
