import axios from "axios";
import React, { createContext, useState } from "react";

export const GlobalContext = createContext();

export const GlobalProvider = (props) => {
    const [data, setData] = useState(null);

    const [fetchStatus, setFectchStatus] = useState(true);

    // handleFunction
    const handleDelete = (event) => {
        let idData = parseInt(event.target.value);

        console.log(idData);

        axios.delete(`https://dev-example.sanbercloud.com/api/job-vacancy/${idData}`).then(() => {
            setFetchStatus(true);
        });
    };

    let state = {
        data,
        setData,
        fetchStatus,
        setFectchStatus,
    };

    let handleFunction = {
        handleDelete,
    };

    return (
        <GlobalContext.Provider
            value={{
                state,
                handleFunction,
            }}
        >
            {props.children}
        </GlobalContext.Provider>
    );
};
