import Cookies from "js-cookie";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = () => {
    const [user, setUser] = useState({});

    useEffect(() => {
        if (Cookies.get("user_data") !== undefined) {
            setUser(JSON.parse(Cookies.get("user_data")));
        }
    }, []);

    return (
        <>
            <div className="relative hidden h-screen shadow-lg lg:block w-80">
                <div className="h-full bg-white dark:bg-gray-700">
                    <div className="flex items-center justify-center pt-6">
                        <p className="text-xl font-bold dark:text-white">Plannifer</p>
                    </div>
                    <div className="grid items-center justify-center pt-6">
                        <img class="w-20 h-20 mb-3 rounded-full shadow-lg" src={user.image_url ?? "undifined"} />
                        <p className="text-primary font-semibold text-center">{user.name ?? "undifined"}</p>
                    </div>
                    <nav className="mt-6">
                        <div>
                            <Link to="/dashboard" className="flex items-center justify-start w-full p-2 pl-6 my-2 text-gray-800 transition-colors duration-200 border-l-4 border-primary ">
                                <span className="text-left">
                                    <svg width={20} height={20} fill="currentColor" viewBox="0 0 1792 1792" xmlns="http://www.w3.org/2000/svg">
                                        <path d="M1472 992v480q0 26-19 45t-45 19h-384v-384h-256v384h-384q-26 0-45-19t-19-45v-480q0-1 .5-3t.5-3l575-474 575 474q1 2 1 6zm223-69l-62 74q-8 9-21 11h-3q-13 0-21-7l-692-577-692 577q-12 8-24 7-13-2-21-11l-62-74q-8-10-7-23.5t11-21.5l719-599q32-26 76-26t76 26l244 204v-195q0-14 9-23t23-9h192q14 0 23 9t9 23v408l219 182q10 8 11 21.5t-7 23.5z"></path>
                                    </svg>
                                </span>
                                <span className="mx-2 text-sm font-normal">Dashboard</span>
                            </Link>
                        </div>
                    </nav>
                </div>
            </div>
        </>
    );
};

export default Sidebar;
