import axios from "axios";
import { Table } from "flowbite-react";
import React, { useContext, useEffect } from "react";
import { GlobalContext } from "../../context/globalcontext";

const ListData = () => {
    const { state } = useContext(GlobalContext);

    let { data, setData } = state;

    useEffect(() => {
        axios
            .get(" https://dev-example.sanbercloud.com/api/job-vacancy")
            .then((res) => {
                // console.log(res.data.data);
                // let results = res.data.data;
                // setData([...results]);
                setData([...res.data.data]);
            })
            .catch((err) => {});
    }, []);

    const description = (str) => {
        if (str.length <= 20) {
            return str;
        }
        return str.slice(0, 20) + "...";
    };

    const currency = (number) => {
        if (number !== 0) {
            return new Intl.NumberFormat("id-ID", {
                style: "currency",
                currency: "IDR",
                maximumSignificantDigits: 3,
            }).format(number);
        } else {
            return "Free";
        }
    };

    return (
        <Table hoverable={true}>
            <Table.Head>
                <Table.HeadCell>Product name</Table.HeadCell>
                <Table.HeadCell>Color</Table.HeadCell>
                <Table.HeadCell>Category</Table.HeadCell>
                <Table.HeadCell>Price</Table.HeadCell>
                <Table.HeadCell>
                    <span className="sr-only">Edit</span>
                </Table.HeadCell>
            </Table.Head>
            <Table.Body className="divide-y">
                <Table.Row className="bg-white dark:border-gray-700 dark:bg-gray-800">
                    <Table.Cell className="whitespace-nowrap font-medium text-gray-900 dark:text-white">Apple MacBook Pro 17"</Table.Cell>
                    <Table.Cell>Sliver</Table.Cell>
                    <Table.Cell>Laptop</Table.Cell>
                    <Table.Cell>$2999</Table.Cell>
                    <Table.Cell>
                        <a href="/tables" className="font-medium text-blue-600 hover:underline dark:text-blue-500">
                            Edit
                        </a>
                    </Table.Cell>
                </Table.Row>
                <Table.Row className="bg-white dark:border-gray-700 dark:bg-gray-800">
                    <Table.Cell className="whitespace-nowrap font-medium text-gray-900 dark:text-white">Microsoft Surface Pro</Table.Cell>
                    <Table.Cell>White</Table.Cell>
                    <Table.Cell>Laptop PC</Table.Cell>
                    <Table.Cell>$1999</Table.Cell>
                    <Table.Cell>
                        <a href="/tables" className="font-medium text-blue-600 hover:underline dark:text-blue-500">
                            Edit
                        </a>
                    </Table.Cell>
                </Table.Row>
                <Table.Row className="bg-white dark:border-gray-700 dark:bg-gray-800">
                    <Table.Cell className="whitespace-nowrap font-medium text-gray-900 dark:text-white">Magic Mouse 2</Table.Cell>
                    <Table.Cell>Black</Table.Cell>
                    <Table.Cell>Accessories</Table.Cell>
                    <Table.Cell>$99</Table.Cell>
                    <Table.Cell>
                        <a href="/tables" className="font-medium text-blue-600 hover:underline dark:text-blue-500">
                            Edit
                        </a>
                    </Table.Cell>
                </Table.Row>
            </Table.Body>
        </Table>
    );
};

export default ListData;
