import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const ListVacancy = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get("https://dev-example.sanbercloud.com/api/job-vacancy").then((res) => {
            setData(res.data.data);
        });
    }, []);
    console.log(data.length);

    const currency = (number) => {
        if (number !== 0) {
            return new Intl.NumberFormat("id-ID", {
                style: "currency",
                currency: "IDR",
                maximumSignificantDigits: 3,
            }).format(number);
        }
    };

    return (
        <>
            <div className="block items-center justify-between py-24 space-y-8 md:space-y-7">
                <div className="flex items-center justify-between">
                    <h1 className="font-bold text-2xl">List lowongan</h1>
                    <Link to="/" className="ml-auto text-primary">
                        selengkapnya
                    </Link>
                </div>
                <div className="flex flex-wrap justify-center gap-4">
                    {data !== null &&
                        data
                            .filter((res, index) => {
                                return index < 3;
                            })
                            .map((res) => {
                                return (
                                    <div key={res.id} className="bg-white w-full lg:max-w-sm rounded-xl shadow-lg border border-gray p-9 ">
                                        <img src={res.company_image_url} className="max-h-20 mb-3" />
                                        <h1 className="text-xl font-bold">{res.title}</h1>
                                        <h2 className="font-semibold">{res.company_name}</h2>
                                        <h2>
                                            {res.job_tenure} - {res.company_city}
                                        </h2>
                                        <p className="mt-4 font-semibold">
                                            {currency(res.salary_min)} - {currency(res.salary_max)}
                                        </p>
                                        <Link to="/" className="text-primary ">
                                            Apply now ...
                                        </Link>
                                    </div>
                                );
                            })}
                </div>
            </div>
        </>
    );
};

export default ListVacancy;
